import os
import sys
from datetime import datetime
import tkinter as tk
import tkinter.ttk as ttk
from functions import PrintLogger, ErrorLogger, set_logfile
from tkinter.constants import *

import v3

def main(*args):
    '''Main entry point for the application.'''
    global root
    root = tk.Tk()

    if not os.path.isdir(r"logs/"):
        os.makedirs("logs")
    set_logfile("Session_"+(datetime.now().strftime("%d%b%y_%H%M%S"))+".log")
    root.protocol( 'WM_DELETE_WINDOW' , root.destroy)
    # Creates a toplevel widget.
    global _top1, _w1
    _top1 = root
    _w1 = v3.mainWindow(_top1)
    sys.stdout = PrintLogger(_w1.scrolltxtStdOut)
    sys.stderr = ErrorLogger(_w1.scrolltxtStdOut)
    root.mainloop()

if __name__ == '__main__':
    v3.start_up()




